","keccak256":"0xa1e8e83cd0087785df04ac79fb395d9f3684caeaf973d9e2c71caef723a3a5d6","license":"MIT"},"contracts/ERC721Promotion.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.14;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "./interfaces/IMessageRecipient.sol";

contract ERC721Promotion is
    ERC721,
    ERC721URIStorage,
    Ownable,
    IMessageRecipient
{
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIdCounter;

    string public badgeTokenURI;
    uint256 public amountOfClaimsPerPerson;
    mapping(address => uint) claimerToClaimsCount;
    address public constant MAILBOX_ADDRESS =
        0xCC737a94FecaeC165AbCf12dED095BB13F037685;
    address public promoter;

    event ClaimedPromotion(address claimer, uint claimCount);

    constructor(
        string memory name,
        string memory symbol,
        string memory _tokenURI,
        address _promoterAddress,
        uint _amountOfClaimsPerPerson
    ) ERC721(name, symbol) {
        badgeTokenURI = _tokenURI;
        amountOfClaimsPerPerson = _amountOfClaimsPerPerson;
        promoter = _promoterAddress;
    }

    modifier onlyMailbox() {
        require(msg.sender == MAILBOX_ADDRESS);
        _;
    }

    function handle(
        uint32 _origin,
        bytes32 _sender,
        bytes calldata _body
    ) external onlyMailbox {
        address claimer = abi.decode(_body, (address));
        if (claimerToClaimsCount[claimer] == 0) {
            safeMint(claimer);
        } else {
            require(
                claimerToClaimsCount[claimer] < amountOfClaimsPerPerson,
                "No more claims"
            );
            addClaim(claimer);
        }
    }

    function safeMint(address to) internal {
        require(claimerToClaimsCount[to] == 0, "Already owns NFT");
        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();

        _safeMint(to, tokenId);
        _setTokenURI(tokenId, badgeTokenURI);
        claimerToClaimsCount[to] = 1;
        emit ClaimedPromotion(to, 1);
    }

    function addClaim(address to) internal {
        claimerToClaimsCount[to] += 1;

        emit ClaimedPromotion(to, claimerToClaimsCount[to]);
    }

    function setTokenURI(string memory _tokenURI) public onlyOwner {
        badgeTokenURI = _tokenURI;
    }

    // Soulbound Token
    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public pure override {
        revert("Cannot transfer");
    }

    // The following functions are overrides required by Solidity.

    function _burn(uint256 tokenId)
        internal
        pure
        override(ERC721, ERC721URIStorage)
    {
        revert("Disabled");
        // super._burn(tokenId);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }
}
","keccak256":"0x749a41fc6f763c57182c0f1e0250b379c48453a25a8de4519f92b4620a1d6feb","license":"MIT"},"contracts/PromotionMain.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.14;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

import "./interfaces/IInterchainQueryRouter.sol";
import "./interfaces/IMailbox.sol";
import "./interfaces/IInterchainGasPaymaster.sol";
import "./interfaces/IReceiver.sol";
import "./ERC721Promotion.sol";

contract PromotionMain is Ownable, ReentrancyGuard {
    using ECDSA for bytes32;
    struct Receiver {
        bytes32 destinationReceiverAddress;
        bool isExists;
    }

    struct Promotion {
        address promotionAddress;
        uint32 destinationDomain;
        address creator;
        uint createdAt;
        uint claimsPerPerson;
        bool isExists;
    }

    struct Claim {
        uint32 destinationDomain;
        address promotionAddress;
        uint claimsCount;
        bool isExists;
    }

    mapping(bytes32 => Promotion) public promotions; // Promotion Id => Promotion
    mapping(bytes32 => mapping(address => Claim)) public claims;

    mapping(bytes32 => uint256) public gasTank;
    mapping(uint32 => Receiver) public chains;

    IMailbox public constant mailbox =
        IMailbox(0xCC737a94FecaeC165AbCf12dED095BB13F037685);
    IInterchainGasPaymaster public constant igp =
        IInterchainGasPaymaster(0xF90cB82a76492614D07B82a7658917f3aC811Ac1);
    IInterchainQueryRouter public constant iqsRouter =
        IInterchainQueryRouter(0xF782C6C4A02f2c71BB8a1Db0166FAB40ea956818);

    event NewChainAdded(uint32 destinationDomain);
    event GasTankFilled(bytes32 promotionId, uint256 amount);
    event GasTankRefunded(bytes32 promotionId, uint256 amount);
    event PromotionCreated(
        bytes32 promotionId,
        address promotionAddress,
        uint32 destinationDomain,
        address creator,
        uint createdAt,
        uint claimsPerPerson,
        string badgeURI
    );
    event ClaimedPromotion(
        bytes32 promotionId,
        address promotionAddress,
        address claimer,
        uint32 destinationDomain,
        uint claimsCount,
        uint claimedAt
    );

    function addChain(uint32 destinationDomain, address destinationReceiver)
        public
        onlyOwner
    {
        chains[destinationDomain] = Receiver(
            addressToBytes32(destinationReceiver),
            true
        );
    }

    function _getAddress(
        string memory name,
        string memory symbol,
        uint32 destinationDomain,
        uint claimsPerPerson,
        string memory badgeURI,
        uint _salt
    ) internal view returns (address) {
        bytes memory bytecode = abi.encodePacked(
            type(ERC721Promotion).creationCode,
            abi.encode(name, symbol, badgeURI, msg.sender, claimsPerPerson)
        );
        bytes32 hash = keccak256(
            abi.encodePacked(
                bytes1(0xff),
                chains[destinationDomain].destinationReceiverAddress,
                bytes32(_salt),
                keccak256(bytecode)
            )
        );

        return address(uint160(uint(hash)));
    }

    function createPromotion(
        string memory name,
        string memory symbol,
        uint32 destinationDomain,
        uint claimsPerPerson,
        string memory badgeURI,
        uint _salt,
        uint gasAmount
    ) public payable {
        require(chains[destinationDomain].isExists, "Invalid Destination");
        // 1. Create PromotionNFT contract in destination chain and Fill Gas
        bytes memory message = abi.encode(
            name,
            symbol,
            badgeURI,
            msg.sender,
            claimsPerPerson,
            bytes32(_salt)
        );

        bytes32 messageId = mailbox.dispatch(
            destinationDomain,
            chains[destinationDomain].destinationReceiverAddress,
            message
        );
        address _promotionAddress = _getAddress(
            name,
            symbol,
            destinationDomain,
            claimsPerPerson,
            badgeURI,
            _salt
        );
        bytes32 _promotionId = keccak256(
            abi.encodePacked(
                name,
                symbol,
                _promotionAddress,
                destinationDomain,
                claimsPerPerson,
                badgeURI,
                msg.sender,
                block.timestamp
            )
        );
        require(promotions[_promotionId].isExists == false, "Promotion exists");

        // 2. Get gas amount
        uint256 quotedPayment = getQuotedPayment(destinationDomain, gasAmount);
        require(msg.value >= quotedPayment, "Insufficient gas");
        gasTank[_promotionId] += (msg.value - quotedPayment);

        // 3. Pay for Interchain Gas
        igp.payForGas{value: quotedPayment}(
            messageId,
            destinationDomain,
            gasAmount,
            msg.sender
        );

        // 4. Update State Variables
        promotions[_promotionId] = Promotion(
            _promotionAddress,
            destinationDomain,
            msg.sender,
            block.timestamp,
            claimsPerPerson,
            true
        );

        // 5. Emit events
        if (gasTank[_promotionId] > 0) {
            emit GasTankFilled(_promotionId, gasTank[_promotionId]);
        }
        emit PromotionCreated(
            _promotionId,
            _promotionAddress,
            destinationDomain,
            msg.sender,
            block.timestamp,
            claimsPerPerson,
            badgeURI
        );
    }

    function claimPromotion(
        bytes32 _promotionId,
        uint32 destinationDomain,
        bytes calldata signature,
        address claimer,
        uint gasAmount
    ) public {
        require(chains[destinationDomain].isExists, "Invalid Destination");
        Promotion memory _promotion = promotions[_promotionId];
        Claim memory _claim = claims[_promotionId][claimer];
        bytes32 hash = keccak256(
            abi.encodePacked(
                _promotionId,
                destinationDomain,
                _promotion.promotionAddress,
                msg.sender
            )
        );
        require(hash.recover(signature) == claimer, "unauthorized");
        require(_promotion.isExists, "Invalid Promotion");
        require(
            !_claim.isExists || _promotion.claimsPerPerson > _claim.claimsCount,
            "No more claims"
        );

        bytes memory message = abi.encode(claimer);
        bytes32 messageId = mailbox.dispatch(
            destinationDomain,
            addressToBytes32(_promotion.promotionAddress),
            message
        );

        uint256 quotedPayment = getQuotedPayment(destinationDomain, gasAmount);
        require(gasTank[_promotionId] >= quotedPayment, "Insufficient gas");
        gasTank[_promotionId] -= quotedPayment;
        igp.payForGas{value: quotedPayment}(
            messageId,
            destinationDomain,
            gasAmount,
            _promotion.creator
        );
        uint _currentClaims = _claim.claimsCount;
        claims[_promotionId][claimer] = Claim(
            destinationDomain,
            _promotion.promotionAddress,
            _currentClaims + 1,
            true
        );

        emit ClaimedPromotion(
            _promotionId,
            _promotion.promotionAddress,
            msg.sender,
            destinationDomain,
            _currentClaims + 1,
            block.timestamp
        );
    }

    function fillGas(bytes32 _promotionId) public payable {
        gasTank[_promotionId] += msg.value;
        emit GasTankFilled(_promotionId, gasTank[_promotionId]);
    }

    function refundGas(bytes32 _promotionId, address _to) public nonReentrant {
        require(msg.sender == promotions[_promotionId].creator, "Unauthorized");
        require(gasTank[_promotionId] > 0, "No balance");
        (bool success, bytes memory data) = payable(_to).call{
            value: gasTank[_promotionId]
        }("");
        if (success) {
            uint _gasAmount = gasTank[_promotionId];
            gasTank[_promotionId] = 0;
            emit GasTankRefunded(_promotionId, _gasAmount);
        } else {
            revert("Failed");
        }
    }

    // GETTERS
    function getQuotedPayment(uint32 destinationDomain, uint gasAmount)
        public
        view
        returns (uint256)
    {
        uint256 quotedPayment = igp.quoteGasPayment(
            destinationDomain,
            gasAmount
        );
        return quotedPayment;
    }

    // LIBRARY FUNCTIONS

    function addressToBytes32(address _addr) internal pure returns (bytes32) {
        return bytes32(uint256(uint160(_addr)));
    }
}
","keccak256":"0xb0b4f0b8a72d4363bf30c2fb400ca6c8d2d2b400524ce27916e20441408a92d3","license":"MIT"},"contracts/interfaces/IInterchainGasPaymaster.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.6.11;

/**
 * @title IInterchainGasPaymaster
 * @notice Manages payments on a source chain to cover gas costs of relaying
 * messages to destination chains.
 */
interface IInterchainGasPaymaster {
    /**
     * @notice Emitted when a payment is made for a message's gas costs.
     * @param messageId The ID of the message to pay for.
     * @param gasAmount The amount of destination gas paid for.
     * @param payment The amount of native tokens paid.
     */
    event GasPayment(
        bytes32 indexed messageId,
        uint256 gasAmount,
        uint256 payment
    );

    function payForGas(
        bytes32 _messageId,
        uint32 _destinationDomain,
        uint256 _gasAmount,
        address _refundAddress
    ) external payable;

    function quoteGasPayment(uint32 _destinationDomain, uint256 _gasAmount)
        external
        view
        returns (uint256);
}
","keccak256":"0x69780ca2e1ee4defcb8bf53175b6832a01b7232726c103082b31eb1ac7c33227","license":"MIT"},"contracts/interfaces/IInterchainQueryRouter.sol":{"content":"interface IInterchainQueryRouter {
    /**
     * @param _destinationDomain Domain of destination chain
     * @param target The address of the contract to query on destination chain.
     * @param queryData The calldata of the view call to make on the destination
     * chain.
     * @param callback Callback function selector on `msg.sender` and optionally
     * abi-encoded prefix arguments.
     * @return messageId The ID of the Hyperlane message encoding the query.
     */
    function query(
        uint32 _destinationDomain,
        address target,
        bytes calldata queryData,
        bytes calldata callback
    ) external returns (bytes32);
}
