pragma solidity ^0.8.14;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "./interfaces/IMessageRecipient.sol";

contract ERC721Promotion is
    ERC721,
    ERC721URIStorage,
    Ownable,
    IMessageRecipient
{
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIdCounter;

    string public badgeTokenURI;
    uint256 public amountOfClaimsPerPerson;
    mapping(address => uint) claimerToClaimsCount;
    address public constant MAILBOX_ADDRESS =
        0xCC737a94FecaeC165AbCf12dED095BB13F037685;
    address public promoter;

    event ClaimedPromotion(address claimer, uint claimCount);

    constructor(
        string memory name,
        string memory symbol,
        string memory _tokenURI,
        address _promoterAddress,
        uint _amountOfClaimsPerPerson
    ) ERC721(name, symbol) {
        badgeTokenURI = _tokenURI;
        amountOfClaimsPerPerson = _amountOfClaimsPerPerson;
        promoter = _promoterAddress;
    }

    modifier onlyMailbox() {
        require(msg.sender == MAILBOX_ADDRESS);
        _;
    }

    function handle(
        uint32 _origin,
        bytes32 _sender,
        bytes calldata _body
    ) external onlyMailbox {
        address claimer = abi.decode(_body, (address));
        if (claimerToClaimsCount[claimer] == 0) {
            safeMint(claimer);
        } else {
            require(
                claimerToClaimsCount[claimer] < amountOfClaimsPerPerson,
                "No more claims"
            );
            addClaim(claimer);
        }
    }

    function safeMint(address to) internal {
        require(claimerToClaimsCount[to] == 0, "Already owns NFT");
        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();

        _safeMint(to, tokenId);
        _setTokenURI(tokenId, badgeTokenURI);
        claimerToClaimsCount[to] = 1;
        emit ClaimedPromotion(to, 1);
    }

    function addClaim(address to) internal {
        claimerToClaimsCount[to] += 1;

        emit ClaimedPromotion(to, claimerToClaimsCount[to]);
    }

    function setTokenURI(string memory _tokenURI) public onlyOwner {
        badgeTokenURI = _tokenURI;
    }

    // Soulbound Token
    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public pure override {
        revert("Cannot transfer");
    }

    // The following functions are overrides required by Solidity.

    function _burn(uint256 tokenId)
        internal
        pure
        override(ERC721, ERC721URIStorage)
    {
        revert("Disabled");
        // super._burn(tokenId);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }
}
","keccak256":"0x749a41fc6f763c57182c0f1e0250b379c48453a25a8de4519f92b4620a1d6feb","license":"MIT"},"contracts/Receiver.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.14;

import "./interfaces/IMessageRecipient.sol";
import "./ERC721Promotion.sol";

contract Receiver is IMessageRecipient {
    mapping(address => address) public latestPromotionDeployment;
    mapping(address => address) public deploymentCreator;
    address public constant MAILBOX_ADDRESS =
        0xCC737a94FecaeC165AbCf12dED095BB13F037685;

    event PromotionNFTDeployed(address promotionNFTAddress);
    event TokenURIChanged(
        address contractAddress,
        address creator,
        string newURI
    );

    modifier onlyMailbox() {
        require(msg.sender == MAILBOX_ADDRESS);
        _;
    }

    function handle(
        uint32 _origin,
        bytes32 _sender,
        bytes calldata _body
    ) external onlyMailbox {
        (
            string memory _name,
            string memory _symbol,
            string memory _badgeURI,
            address _promoterAddress,
            uint _claimsPerPerson,
            bytes32 _salt
        ) = abi.decode(_body, (string, string, string, address, uint, bytes32));
        ERC721Promotion promotion = (new ERC721Promotion){salt: _salt}(
            _name,
            _symbol,
            _badgeURI,
            _promoterAddress,
            _claimsPerPerson
        );
        emit PromotionNFTDeployed(address(promotion));
    }

    function changeTokenURI(address contractAddress, string calldata newURI)
        public
    {
        require(msg.sender == deploymentCreator[contractAddress], "Not owned");
        ERC721Promotion(contractAddress).setTokenURI(newURI);

        emit TokenURIChanged(contractAddress, msg.sender, newURI);
    }

    function getLatestPromotionDeployment(address promoter)
        public
        view
        returns (address)
    {
        return latestPromotionDeployment[promoter];
    }

    function bytes32ToString(bytes32 _bytes32)
        public
        pure
        returns (string memory)
    {
        uint8 i = 0;
        while (i < 32 && _bytes32[i] != 0) {
            i++;
        }
        bytes memory bytesArray = new bytes(i);
        for (i = 0; i < 32 && _bytes32[i] != 0; i++) {
            bytesArray[i] = _bytes32[i];
        }
        return string(bytesArray);
    }
}
